import React, { useEffect, useState, useContext } from 'react';
import API from '../api/api';
import { AuthContext } from '../context/AuthContext';

const Feed = () => {
  const { user } = useContext(AuthContext);
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [content, setContent] = useState('');
  const [image, setImage] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const res = await API.get('/posts');
        setPosts(res.data);
      } catch (err) {
        // handle error
      } finally {
        setLoading(false);
      }
    };
    fetchPosts();
  }, []);

  const handlePost = async (e) => {
    e.preventDefault();
    if (!content.trim()) return;
    try {
      const res = await API.post('/posts', { content, image });
      setPosts([res.data, ...posts]);
      setContent('');
      setImage('');
      setError('');
    } catch (err) {
      setError('Failed to create post');
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      <h2>Feed</h2>
      {user && (
        <form onSubmit={handlePost} style={{marginBottom:'20px'}}>
          <textarea
            placeholder="What's on your mind?"
            value={content}
            onChange={e => setContent(e.target.value)}
            required
            style={{width:'100%',minHeight:'60px'}}
          />
          <input
            type="text"
            placeholder="Image URL (optional)"
            value={image}
            onChange={e => setImage(e.target.value)}
            style={{width:'100%',marginTop:'5px'}}
          />
          <button type="submit">Post</button>
          {error && <span style={{color:'red'}}>{error}</span>}
        </form>
      )}
      {posts.length === 0 ? (
        <p>No posts yet.</p>
      ) : (
        posts.map(post => (
          <div key={post._id} style={{border:'1px solid #ccc', margin:'10px', padding:'10px'}}>
            <div><b>{post.author.username}</b></div>
            <div>{post.content}</div>
            {post.image && <img src={post.image} alt="post" style={{maxWidth:'200px'}} />}
            <div style={{fontSize:'0.8em', color:'#888'}}>{new Date(post.createdAt).toLocaleString()}</div>
          </div>
        ))
      )}
    </div>
  );
};

export default Feed;
