import React, { useEffect, useState, useContext } from 'react';
import API from '../api/api';
import { AuthContext } from '../context/AuthContext';
import DefaultAvatar from '../components/DefaultAvatar';
import './Profile.css';

const Profile = () => {
  const { user, token, login } = useContext(AuthContext);
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState('');
  const [posts, setPosts] = useState([]);
  const [editOpen, setEditOpen] = useState(false);
  const [editUsername, setEditUsername] = useState('');
  const [editAvatar, setEditAvatar] = useState('');
  const [editError, setEditError] = useState('');
  const [editLoading, setEditLoading] = useState(false);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await API.get(`/users/profile/${user.id}`);
        setProfile(res.data);
      } catch (err) {
        setError('Failed to load profile');
      }
    };
    const fetchPosts = async () => {
      try {
        const res = await API.get('/posts');
        setPosts(res.data.filter(p => p.author._id === user.id));
      } catch {}
    };
    if (user && token) {
      fetchProfile();
      fetchPosts();
    }
  }, [user, token]);

  const openEdit = () => {
    setEditUsername(profile.username);
    setEditAvatar(profile.avatar || '');
    setEditOpen(true);
    setEditError('');
  };

  const handleEdit = async (e) => {
    e.preventDefault();
    setEditLoading(true);
    try {
      const res = await API.put('/users/profile', { username: editUsername, avatar: editAvatar });
      setProfile(res.data);
      login(res.data, token);
      setEditOpen(false);
    } catch (err) {
      setEditError(err.response?.data?.error || 'Update failed');
    } finally {
      setEditLoading(false);
    }
  };

  if (!user) return <div>Please log in.</div>;
  if (error) return <div>{error}</div>;
  if (!profile) return <div>Loading...</div>;

  return (
    <div className="profile-container">
      <div className="profile-card">
        {profile.avatar ? (
          <img src={profile.avatar} alt="avatar" className="profile-avatar" />
        ) : (
          <DefaultAvatar size={100} />
        )}
        <div className="profile-info">
          <div className="profile-username">{profile.username}</div>
          <div className="profile-email">{profile.email}</div>
        </div>
        <button className="profile-edit-btn" onClick={openEdit}>Edit Profile</button>
      </div>
      {editOpen && (
        <div className="profile-edit-modal">
          <form className="profile-edit-form" onSubmit={handleEdit}>
            <h3>Edit Profile</h3>
            <input
              type="text"
              value={editUsername}
              onChange={e => setEditUsername(e.target.value)}
              placeholder="Username"
              required
            />
            <input
              type="text"
              value={editAvatar}
              onChange={e => setEditAvatar(e.target.value)}
              placeholder="Avatar URL"
            />
            <div className="profile-edit-actions">
              <button type="button" onClick={() => setEditOpen(false)} disabled={editLoading}>Cancel</button>
              <button type="submit" disabled={editLoading}>{editLoading ? 'Saving...' : 'Save'}</button>
            </div>
            {editError && <div className="profile-edit-error">{editError}</div>}
          </form>
        </div>
      )}
      <div className="profile-posts">
        <h3>Your Posts</h3>
        {posts.length === 0 ? (
          <div className="no-posts">No posts yet.</div>
        ) : (
          posts.map(post => (
            <div key={post._id} className="profile-post-card">
              <div className="profile-post-content">{post.content}</div>
              {post.image && <img src={post.image} alt="post" className="profile-post-image" />}
              <div className="profile-post-time">{new Date(post.createdAt).toLocaleString()}</div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Profile;
